// Living Dead
<Custom Apply Effect>
if (user.isStateAffected(141) && !user._walkingDeadFailed) {
  user.removeState(1);
  user.removeState(141);
  user.addState(142);
  user.setHp(1);
}
</Custom Apply Effect>

// Death Injuries
<Custom Apply Effect>
if (user.isDead()) {
var possibleInjuries = [];
possibleInjuries.push(89, 90, 91, 92);
possibleInjuries.push(93, 94, 95, 96);
possibleInjuries.push(97, 98, 99, 100);
possibleInjuries.push(101, 102, 103, 104);
for (var i = 0; i < possibleInjuries.length; ++i) {
  var j = Math.floor(Math.random() * (i + 1));
  var temp = possibleInjuries[i];
  possibleInjuries[i] = possibleInjuries[j];
  possibleInjuries[j] = temp;
}
var received = 1;
while (received) {
  if (possibleInjuries.length > 0) {
    var stateId = possibleInjuries.shift();
    if (!target.isStateAffected(stateId)) {
      target.addNewState(stateId);
      received -= 1;
    }
  } else {
    break;
  }
}
} // user.isDead()
</Custom Apply Effect>

// Death Nova
<Custom Apply Effect>
var deathNovaId = 271;
if (target.isDead() && target.isStateAffected(deathNovaId)) {
  var poisonId = 37;
  var caster = target.stateOrigin(deathNovaId);
  var dmg = caster.mat * 6;
  var foes = target.opponentsUnit().aliveMembers();
  for (var i = 0; i < foes.length; ++i) {
    var foe = foes[i];
    if (foe) {
      foe.gainHp(-dmg);
      foe.addState(poisonId);
      foe.startDamagePopup();
      if (foe.isDead()) {
        foe.performCollapse();
      }
      foe.clearResult();
    }
  }
  target.startAnimation(109);
  target.removeState(deathNovaId);
}
</Custom Apply Effect>

// Soul Heart Passive
<Custom Apply Effect>
if (user.isDead()) {
  var soulHeartId = 329;
  var group = user.friendsUnit().aliveMembers().concat(user.opponentsUnit().aliveMembers());
  while (group.length > 0) {
    var member = group.shift();
    if (member && member.isStateAffected(soulHeartId)) {
      var paramId = 4;
      var turns = 5;
      member.addBuff(paramId, turns);
    }
  }
}
</Custom Apply Effect>

// Critical Bonus
<Custom Establish Effect>
if (this.isPhysical() && target.result().hpDamage > 0 && target.result().critical) {
  var param = 2;
  var turns = 5;
  user.addBuff(param, turns);
  var param = 6;
  var turns = 5;
  user.addBuff(param, turns);
  if (target.isAlive()) {
    var param = 3;
    var turns = 5;
    target.addDebuff(param, turns);
    var param = 6;
    var turns = 5;
    target.addDebuff(param, turns);
  }
}
</Custom Establish Effect>

